import React from "react";
import axios from "axios";
import styled from "styled-components";
import { FaTrash, FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";

// Estiliza a tabela
const Table = styled.table`
  width: 150%;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  max-width: 1120px;
  margin: 20px auto;
  word-break: break-all;
  border-collapse: collapse; /* Para garantir que as bordas colidem */
`;

// Estiliza os elementos da tabela
const Thead = styled.thead`
  display: flex;
  flex-wrap: wrap; /* Permite que os cabeçalhos se ajustem em linhas */
`;

const Tbody = styled.tbody``;

const Tr = styled.tr`
  width: 100%;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  border-bottom: 1px solid #ddd; /* Adiciona borda inferior para cada linha */
`;

const Th = styled.th`
  text-align: left;
  padding: 10px;
  flex: 1; /* Distribui igualmente os cabeçalhos */
`;

const Td = styled.td`
  padding: 5px;
  text-align: left;
  flex: 1; /* Distribui igualmente as células */
`;

const Grid = ({ users, setUsers, setOnEdit }) => {
  const handleEdit = (item) => {
    setOnEdit(item);
  };

  const handleDelete = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:8800/${id}`);
      if (response.status === 200) {
        const newArray = users.filter((user) => user.id !== id);
        setUsers(newArray);
        toast.success(response.data);
      }
    } catch (error) {
      toast.error(error.response.data);
    }
    setOnEdit(null);
  };

  return (
    <Table>
      <Thead>
        <Tr>
          <Th>Nome</Th>
          <Th>Sobrenome</Th>
          <Th>Cpf</Th>
          <Th>Telefone</Th>
          <Th onlyWeb>E-mail</Th>
          <Th onlyWeb>Senha</Th>
          <Th onlyWeb>Endereço</Th>
          <Th></Th>
          <Th></Th>
        </Tr>
      </Thead>
      <Tbody>
        {users.map((item, i) => (
          <Tr key={i}>
            <Td>{item.nome}</Td>
            <Td>{item.sobrenome}</Td>
            <Td>{item.cpf}</Td>
            <Td>{item.celular}</Td>
            <Td onlyWeb>{item.email}</Td>
            <Td onlyWeb>{item.senha}</Td>
            <Td onlyWeb>{item.endereco}</Td>
            <Td alignCenter>
              <FaEdit onClick={() => handleEdit(item)} style={{ cursor: "pointer" }} />
            </Td>
            <Td alignCenter>
              <FaTrash onClick={() => handleDelete(item.id)} style={{ cursor: "pointer" }} />
            </Td>
          </Tr>
        ))}
      </Tbody>
    </Table>
  );
};

export default Grid;
