// Importações necessárias
import React from "react"; // Biblioteca principal do React
import axios from "axios"; // Biblioteca para fazer requisições HTTP
import styled from "styled-components"; // Biblioteca para criar componentes estilizados
import { FaTrash, FaEdit } from "react-icons/fa"; // Ícones de edição e exclusão
import { toast } from "react-toastify"; // Biblioteca para exibir notificações

// Definição de um componente estilizado para a tabela
const Table = styled.table`
  width: 100%;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  max-width: 1120px;
  margin: 20px auto;
  word-break: break-all;
`;

// Componentes estilizados para as partes da tabela
export const Thead = styled.thead``;
export const Tbody = styled.tbody``;
export const Tr = styled.tr``;

// Componente estilizado para as células de cabeçalho da tabela
export const Th = styled.th`
  text-align: start;
  border-bottom: inset;
  padding-bottom: 5px;

  @media (max-width: 500px) {
    ${(props) => props.onlyWeb && "display: none"}
  }
`;

// Componente estilizado para as células de dados da tabela
export const Td = styled.td`
  padding-top: 15px;
  text-align: ${(props) => (props.alignCenter ? "center" : "start")};
  width: ${(props) => (props.width ? props.width : "auto")};

  @media (max-width: 500px) {
    ${(props) => props.onlyWeb && "display: none"}
  }
`;

// Definição do componente Grid
const Grid = ({ users, setUsers, setOnEdit }) => {
  // Função para lidar com a edição de um item
  const handleEdit = (item) => {
    setOnEdit(item);
  };

  // Função para lidar com a exclusão de um item
  const handleDelete = async (id) => {
    await axios
      .delete("http://localhost:8800/" + id) // Faz uma requisição DELETE para remover o usuário
      .then(({ data }) => {
        const newArray = users.filter((user) => user.id !== id); // Filtra a lista de usuários para remover o usuário excluído

        setUsers(newArray); // Atualiza a lista de usuários
        toast.success(data); // Exibe uma notificação de sucesso
      })
      .catch(({ data }) => toast.error(data)); // Exibe uma notificação de erro

    setOnEdit(null); // Reseta o estado de edição
  };

  // Renderiza a tabela com os usuários
  return (
    <Table>
      <Thead>
        <Tr>
          <Th>Nome</Th>
          <Th>Sobrenome</Th>
          <Th>Cpf</Th>
          <Th>Telefone</Th>
          <Th onlyWeb>E-mail</Th>
          <Th onlyWeb>Senha</Th>
          <Th onlyWeb>Endereco</Th>
          <Th></Th>
          <Th></Th>
        </Tr>
      </Thead>
      <Tbody>
        {users.map((item, i) => (
          <Tr key={i}>
            <Td width="10%">{item.nome}</Td>
            <Td width="15%">{item.sobrenome}</Td>
            <Td width="10%">{item.cpf}</Td>
            <Td width="15%">{item.celular}</Td>
            <Td width="30%" onlyWeb>{item.email}</Td>
            <Td width="15%" onlyWeb>{item.senha}</Td>
            <Td width="15%" onlyWeb>{item.endereco}</Td>
            <Td alignCenter width="5%">
              <FaEdit onClick={() => handleEdit(item)} /> {/* Ícone de edição */}
            </Td>
            <Td alignCenter width="5%">
              <FaTrash onClick={() => handleDelete(item.id)} /> {/* Ícone de exclusão */}
            </Td>
          </Tr>
        ))}
      </Tbody>
    </Table>
  );
};

// Exporta o componente Grid como padrão
export default Grid;
