import React from "react";
import axios from "axios";
import styled from "styled-components";
import { FaTrash, FaEdit } from "react-icons/fa";
import { toast } from "react-toastify";

const Table = styled.table`
  width: 100%;
  background-color: #fff;
  padding: 20px;
  box-shadow: 0px 0px 5px #ccc;
  border-radius: 5px;
  max-width: 1120px;
  margin: 20px auto;
  word-break: break-all;
`;

const Tbody = styled.tbody``;
const Tr = styled.tr``;
const Th = styled.th`
  text-align: left;
  border-bottom: 1px solid #ddd;
  padding: 10px;
`;

const Td = styled.td`
  padding: 10px;
  text-align: left;
  border-bottom: 1px solid #ddd;
`;

const Grid = ({ users, setUsers, setOnEdit }) => {
  const handleEdit = (item) => {
    setOnEdit(item);
  };

  const handleDelete = async (id) => {
    await axios
      .delete("http://localhost:8800/" + id)
      .then(({ data }) => {
        const newArray = users.filter((user) => user.id !== id);
        setUsers(newArray);
        toast.success(data);
      })
      .catch(({ data }) => toast.error(data));
    setOnEdit(null);
  };

  return (
    <Table>
      <thead>
        <Tr>
          <Th>Nome</Th>
          <br></br>
          <br></br>
          <br></br>
          <br></br>
          <Th>Sobrenome</Th>
          <Th>Cpf</Th>
          <Th>Telefone</Th>
          <Th onlyWeb>E-mail</Th>
          <Th onlyWeb>Senha</Th>
          <Th onlyWeb>Endereço</Th>
          <Th></Th>
          <Th></Th>
        </Tr>
      </thead>
      <Tbody>
        {users.map((item, i) => (
          <Tr key={i}>
            <Td>{`Nome: ${item.nome}`}</Td>
            <Td>{`Sobrenome: ${item.sobrenome}`}</Td>
            <Td>{`Cpf: ${item.cpf}`}</Td>
            <Td>{`Telefone: ${item.celular}`}</Td>
            <Td onlyWeb>{`E-mail: ${item.email}`}</Td>
            <Td onlyWeb>{`Senha: ${item.senha}`}</Td>
            <Td onlyWeb>{`Endereço: ${item.endereco}`}</Td>
            <Td alignCenter>
              <FaEdit onClick={() => handleEdit(item)} style={{ cursor: "pointer" }} />
            </Td>
            <Td alignCenter>
              <FaTrash onClick={() => handleDelete(item.id)} style={{ cursor: "pointer" }} />
            </Td>
          </Tr>
        ))}
      </Tbody>
    </Table>
  );
};

export default Grid;
